    public void addRoleToUser(String userId, String roleName) {
        String apiUrl = auth0BaseUrl + "/users/" + userId + "/roles";

        HttpHeaders headers = new HttpHeaders();
        headers.set("Authorization", "Bearer " + managementApiToken);
        headers.set("Content-Type", "application/json");

        String body = "[\"" + roleName + "\"]";
        HttpEntity<String> requestEntity = new HttpEntity<>(body, headers);

        RestTemplate restTemplate = new RestTemplate();
        ResponseEntity<String> responseEntity = restTemplate.exchange(apiUrl, HttpMethod.POST, requestEntity, String.class);

        if (responseEntity.getStatusCode().is2xxSuccessful()) {
            System.out.println("Role added successfully.");
        } else {
            System.out.println("Failed to add role. Response: " + responseEntity.getBody());
        }
    }


    public void addRoleToUser1(String userId, String roleName, String managementApiToken) {
        try {
            String apiUrl = "https://dev-gzgdh7hw4udvv70u.us.auth0.com/api/v2/users/" + userId + "/roles";
            URL url = new URL(apiUrl);
            HttpURLConnection connection = (HttpURLConnection) url.openConnection();

            // Set the request method to POST
            connection.setRequestMethod("POST");

            // Set request headers
            connection.setRequestProperty("Content-Type", "application/json");
            connection.setRequestProperty("Authorization", "Bearer " + managementApiToken);
            connection.setRequestProperty("Cache-Control", "no-cache");

            // Enable input/output streams
            connection.setDoOutput(true);

            // Create JSON payload
            String payload = "{ \"roles\": [ \"" + roleName + "\" ] }";

            // Write payload to the request body
            try (DataOutputStream wr = new DataOutputStream(connection.getOutputStream())) {
                wr.writeBytes(payload);
                wr.flush();
            }

            // Get the response code
            int responseCode = connection.getResponseCode();
            System.out.println("Response Code: " + responseCode);

            // Read the response
            try (BufferedReader in = new BufferedReader(new InputStreamReader(connection.getInputStream()))) {
                String inputLine;
                StringBuilder response = new StringBuilder();

                while ((inputLine = in.readLine()) != null) {
                    response.append(inputLine);
                }
                System.out.println("Response Body: " + response.toString());

                // Handle the response based on the status code
                if (responseCode >= 200 && responseCode < 300) {
                    System.out.println("Role added successfully.");
                } else {
                    System.out.println("Failed to add role. Response: " + response.toString());
                }
            }

            // Disconnect the connection
            connection.disconnect();
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

